function barraFija(){const e=document.querySelector(".navegacion"),t=document.querySelector(".seccion");document.addEventListener("scroll",(function(){t.getBoundingClientRect().bottom<1?e.classList.add("fixed"):e.classList.remove("fixed")}))}function crearGaleria(){const e=document.querySelector(".galeria__imagenes");for(let t=1;t<=16;t++){const o=document.createElement("IMG");o.src=`src/img/gallery/full/${t}.jpg`,o.alt="imagen galleria",o.onclick=function(){mostrarImagen(t)},e.appendChild(o)}}function mostrarImagen(e){const t=document.createElement("IMG");t.src=`src/img/gallery/full/${e}.jpg`,t.alt="imagen galleria";const o=document.createElement("DIV");o.classList.add("modal"),o.onclick=cerrarModal,o.appendChild(t);const c=document.querySelector("body");c.classList.add("overflow-hidden"),c.appendChild(o)}function cerrarModal(){const e=document.querySelector(".modal");e.classList.add("fade--out"),setTimeout((()=>{e.remove();document.querySelector("body").classList.remove("overflow-hidden")}),500)}function resaltarEnlace(){document.addEventListener("scroll",(function(){const e=document.querySelectorAll("section"),t=document.querySelectorAll(".navegacion__menu a");let o="";e.forEach((e=>{const t=e.offsetTop,c=e.clientHeight;window.scrollY>=t-c/3&&(o=e.id)})),t.forEach((e=>{e.classList.remove("active"),e.getAttribute("href")==="#"+o&&e.classList.add("active")}))}))}function scrollNav(){document.querySelectorAll(".navegacion__menu").forEach((e=>{e.addEventListener("click",(e=>{e.preventDefault();const t=e.target.getAttribute("href");document.querySelector(t).scrollIntoView({behavior:"smooth"})}))}))}document.addEventListener("DOMContentLoaded",(function(){barraFija(),crearGaleria(),resaltarEnlace(),scrollNav()}));